import { Plugin } from 'vite';
import { ConfigureOptions, Environment, Extension } from 'nunjucks';

declare type templateVariables = Record<string, object>;
interface nunjucksPluginOptions {
    templatesDir?: string;
    variables?: templateVariables;
    nunjucksConfigure?: ConfigureOptions;
    nunjucksEnvironment?: nunjucksEnvironmentOptions | Environment;
}
declare type nunjucksFilterCallback = (...params: any[]) => void;
interface nunjucksFilter {
    async: boolean;
    filter: nunjucksFilterCallback;
}
interface nunjucksEnvironmentOptions {
    filters?: {
        [key: string]: nunjucksFilterCallback | nunjucksFilter;
    };
    extensions?: {
        [key: string]: Extension;
    };
}

declare const globalVariablesKey = "*";
declare const _default: (options?: nunjucksPluginOptions) => Plugin;

export { _default as default, globalVariablesKey };
