import { defineComponent, h } from 'vue';
import { capitalizeFirstLetter } from '../utils.js';
export const adapter = (id, name) => defineComponent({
    name: `Icon${capitalizeFirstLetter(name)}`,
    setup(props, { attrs }) {
        return () => h('svg', {
            width: '1em',
            height: '1em',
            ...attrs,
        }, [
            h('use', { href: `#${id}` }),
        ]);
    },
});
